function notify(i,t){t="undefined"!=typeof t?t:"success";var n=$(".notification");switch(n.show(),n.css("top","-"+n.outerHeight(!0)+"px"),n.attr("class","notification "+t),n.css("left",Math.max(0,($(window).width()-n.outerWidth())/2+$(window).scrollLeft())+"px"),t){case"success":icon="check";break;case"error":icon="exclamation";break;default:icon="question"}$(".notification").find("i").attr("class","fa fa-"+icon),$(".notification").find("span").html(i),n.animate({top:"0"},500),n.delay(2e3).fadeOut(1e3)}$(function(){$("#mobile-menu").click(function(){$(".mobile-menu").slideToggle()})});